from sensor import SENSORfrom motor import MOTORimport pybullet as pimport pybullet_dataimport pyrosim.pyrosim as pyrosimimport constants as cclass ROBOT:    def __init__(self,r):        self.robot = r            def Prepare_To_Sense(self):                self.sensors = {}        for linkName in pyrosim.linkNamesToIndices:            self.sensors[linkName] = SENSOR(linkName)                def Sense(self, timeStep):        for sensor in self.sensors:            self.sensors[sensor].GetValue(timeStep)                def Prepare_To_Act(self):        self.motors = {}        for jointName in pyrosim.jointNamesToIndices:            self.motors[jointName] = MOTOR(jointName)                def Act(self, timeStep):        for motor in self.motors:            self.motors[motor].SetValue(self.robot,timeStep)